//How to update element inside the array at specific position:
let arr3 = [10, 20, 30, "S", 50, 60, 70, 80, 90, 100];
let indexToUpdate = 3; // Index of the element to update
let newValue = "Updated"; // New value to set
arr3[indexToUpdate] = newValue; // Update the element at index 3
console.log(arr3); // Output: [10, 20, 30, "Updated", 50, 60, 70, 80, 90, 100]